import React, { useState, useEffect } from "react";
import "./App.css";
import WorkoutList from "./WorkoutList"

function AthleteContainer () {

    const [athletes, setAthletes] = useState([])
    const [selectedPlayer, setSelectedPlayer] = useState("")

    function handleClick(e) {
        setSelectedPlayer(e.target.id)
    }

    useEffect(() => {
    fetch("http://localhost:9292/")
    .then((res) => res.json())
    .then((data) => setAthletes(data))
}, [])

    return (
        <div>
            {athletes.map((athlete) => <p key={athlete.id} id={athlete.id} onClick={handleClick}>{athlete.name}, {athlete.age}</p>)}
            {selectedPlayer ? <WorkoutList selectedPlayer={athletes[selectedPlayer - 1]}/> : null}
        </div>
    )
}

export default AthleteContainer
