import React, { useState, useEffect } from "react";
import "./App.css";
import WorkoutList from "./WorkoutList"
import AthleteList from "./AthleteList"

function AthleteContainer () {

    const [athletes, setAthletes] = useState([])
    const [selectedAthlete, setSelectedAthlete] = useState("")

    useEffect(() => {
    fetch("http://localhost:9292/")
    .then((res) => res.json())
    .then((data) => setAthletes(data))
}, [])

    function handleLogSubmit(formData) {
        fetch("http://localhost:9292/log_entries"), {
            method: "POST",
            headers: {
                "Content-Type": "application/json",
            },
            body: JSON.stringify({
                "athlete_id": formData.athlete_id,
                "rating": formData.rating,
                "mileage": formData.mileage,
                "details": formData.details,
                "comments": formData.comments
            }),
        }
        .then((res) => res.json())
        .then((data) => console.log(data))
    }

    return (
        <div id="athlete_container">
            <AthleteList athletes={athletes} setSelectedAthlete={setSelectedAthlete}/>
            <WorkoutList onLogSubmit={handleLogSubmit} selectedAthlete={athletes[selectedAthlete - 1]} athletes={athletes}/>
        </div>
    )
}

export default AthleteContainer
