import React, {useState} from "react";
import "./App.css";
import AddLog from "./AddLog";

function Comments( {logEntries, athletes, onLogSubmit, workoutLogs, workoutId} ) {

    console.log(workoutLogs)

    const [logWorkout, setLogWorkout] = useState(false)

    function handleLogWorkoutClick(e) {
        setLogWorkout(!logWorkout)
    }

    const displayLog = workoutLogs.map((entry) => {
        if (entry.workout_id === workoutId) {
        return (
            <div id="comment" key={entry.id}>
                <h4>{athletes[entry.athlete_id - 1].name}</h4>
                <p>workout rating: {entry.workout_rating}</p>
                <p>{entry.details}</p>
                <em>{entry.comments}</em>
                <p>{`${entry.created_at.slice(5, 10)}-${entry.created_at.slice(2,4)}`}</p>
                <p>mileage: {entry.mileage}</p>
            </div>
        )} else null
    }) 

return (
    <div id="comment_container">
        {displayLog}
        <button onClick={handleLogWorkoutClick}>{!logWorkout ? "log this workout" : "close"}</button>
        {logWorkout ? <AddLog setLogWorkout={setLogWorkout} onLogSubmit={onLogSubmit} workoutId={logEntries[0].workout_id}/> : null}
    </div>
  )
}
export default Comments
