import React, {useState} from "react";
import "./App.css";
import PatchAthlete from "./PatchAthlete";


function AthleteList ({ athletes, onAthleteClick, onSelectAll }) {

    const [updateAthlete, setUpdateAthlete] = useState(false)

    function handleClick(e) {
        onAthleteClick(e.target.id)
    }

    function handleAllClick(e){
        onSelectAll("All")
    }

    function handleEditAthleteClick(e) {
        setUpdateAthlete(!updateAthlete)
    }

    const alphaOrder = athletes.sort((a, b) => a.name.localeCompare(b.name) )

    return (
        <div id="athlete_list" >
            <p>Name(id#)</p>
            {alphaOrder.map((athlete) => 
                <div key={athlete.id} >
                    <p className="athlete_name"  id={athlete.id} onClick={handleClick}
                    >
                    {athlete.name} ({athlete.id}) 
                    </p>
                    <button className="athlete_name_button" value={athlete.id} onClick={handleEditAthleteClick}> ‚úçüèΩ</button>
                    <button className="athlete_name_button"> x</button>
                    {updateAthlete ? <PatchAthlete athleteId = {athlete.id}/> : null}
                </div>
            )}
            <p onClick={handleAllClick} className="athlete_name" name="All">Show All</p>
        </div>
    )
}

export default AthleteList
